import React, { useState } from "react";

const EventRegister = () => {
  const [email, setEmail] = useState("");
  const [userStatus, setUserStatus] = useState(null);
  const [loading, setLoading] = useState(false);
  const [userData, setUserData] = useState(null);

  const handleCheckUser = async () => {
    setLoading(true);
    setUserStatus(null);

    try {
      const formData = new FormData();
      formData.append("email", email);

      const res = await fetch("http://localhost/api/check-user.php", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();

      if (data.status === "found") {
        setUserStatus("found");
        setUserData(data.user);
      } else {
        setUserStatus("not_found");
        setUserData(null);
      }
    } catch (error) {
      setUserStatus("error");
    }

    setLoading(false);
  };

  const handlePayment = () => {
    alert("Proceeding to payment gateway...");
    // integrate Razorpay, Stripe, PayPal etc. here
  };

  return (
    <div style={{ padding: "20px", maxWidth: "500px", margin: "0 auto" }}>
      <h2>Event Registration</h2>

      {/* Email input */}
      <input
        type="email"
        value={email}
        placeholder="Enter your email"
        onChange={(e) => setEmail(e.target.value)}
        style={{
          padding: "10px",
          width: "100%",
          marginBottom: "10px",
          border: "1px solid #ccc",
          borderRadius: "5px",
        }}
      />
      <button
        onClick={handleCheckUser}
        disabled={loading || !email}
        style={{
          padding: "10px 20px",
          backgroundColor: "#007bff",
          color: "white",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        {loading ? "Checking..." : "Check User"}
      </button>

      {/* Status messages */}
      {userStatus === "found" && (
        <div style={{ marginTop: "20px", color: "green" }}>
          <h4>User Verified: {userData.name}</h4>
          <p>Email: {userData.email}</p>

          {/* Registration Form */}
          <div style={{ marginTop: "20px" }}>
            <h3>Complete Your Registration</h3>
            <input
              type="text"
              placeholder="Enter your full name"
              style={{
                padding: "10px",
                width: "100%",
                marginBottom: "10px",
                border: "1px solid #ccc",
                borderRadius: "5px",
              }}
            />
            <button
              onClick={handlePayment}
              style={{
                padding: "10px 20px",
                backgroundColor: "green",
                color: "white",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Register & Pay
            </button>
          </div>
        </div>
      )}

      {userStatus === "not_found" && (
        <div style={{ marginTop: "20px", color: "red" }}>
          User not found. You cannot register or make payment.
        </div>
      )}

      {userStatus === "error" && (
        <div style={{ marginTop: "20px", color: "red" }}>
          Something went wrong. Please try again.
        </div>
      )}
    </div>
  );
};

export default EventRegister;
