import React, { useState, useEffect } from "react";
import Select from "react-select";
import "./RegisterModal.css";
import logo from "../assets/logo/logo.png"; // replace with your logo

const RegisterModal = ({ onClose }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    contact: "",
    state: null,
    city: null
  });

  const [states, setStates] = useState([]);
  const [cities, setCities] = useState([]);

  // Fetch states from API when modal opens
  useEffect(() => {
    fetch("http://localhost/react-api/get-indian-states.php") // <-- Replace with your API endpoint
      .then(res => res.json())
      .then(data => {
        const stateOptions = data.map(s => ({ value: s.id, label: s.name }));
        setStates(stateOptions);
      })
      .catch(err => console.error("Error fetching states:", err));
  }, []);

  // Fetch cities when a state is selected
  const handleStateChange = (selectedState) => {
    setFormData({ ...formData, state: selectedState, city: null });
    if (selectedState) {
      fetch(`https://your-api.com/cities?stateId=${selectedState.value}`) // <-- Replace with your API endpoint
        .then(res => res.json())
        .then(data => {
          const cityOptions = data.map(c => ({ value: c.id, label: c.name }));
          setCities(cityOptions);
        })
        .catch(err => console.error("Error fetching cities:", err));
    } else {
      setCities([]);
    }
  };

  const handleCityChange = (selectedCity) => {
    setFormData({ ...formData, city: selectedCity });
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.state || !formData.city) {
      alert("Please select both state and city.");
      return;
    }

    console.log("Registration form submitted:", formData);

    // Example POST request
    fetch("https://your-api.com/register", { // <-- Replace with your API endpoint
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        ...formData,
        state: formData.state.label,
        city: formData.city.label,
        fee: 500
      })
    })
      .then(res => res.json())
      .then(() => {
        alert("Thank you for registering! Your registration fee is ₹500 (non-refundable).");
        onClose();
      })
      .catch(err => console.error("Registration error:", err));
  };

  return (
    <div className="register-modal-backdrop">
      <div className="register-modal">
        <button className="close-btn" onClick={onClose}>
          &times;
        </button>
        <div className="register-header">
          <img src={logo} alt="Franmax Expo Logo" />
          <h2>Register for Franmax Expo 2025</h2>
        </div>

        <div className="register-benefits">
          <h3>Why Attend?</h3>
          <ul>
            <li>Discover top franchise opportunities</li>
            <li>Meet investors and industry experts</li>
            <li>Learn from keynote speakers and workshops</li>
            <li>Network with decision makers</li>
          </ul>
        </div>

        <div className="registration-fee-note">
          <p><strong>Registration Fee: ₹500</strong> (No refund will be given)</p>
        </div>

        <form onSubmit={handleSubmit} className="register-form">
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email Address"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
          <input
            type="tel"
            name="contact"
            placeholder="Contact Number"
            value={formData.contact}
            onChange={handleInputChange}
            required
          />

          <Select
            options={states}
            value={formData.state}
            onChange={handleStateChange}
            placeholder="Select State"
            className="react-select"
            isClearable
            required
          />

          <Select
            options={cities}
            value={formData.city}
            onChange={handleCityChange}
            placeholder="Select City"
            className="react-select"
            isClearable
            isDisabled={!formData.state}
            required
          />

          <button type="submit" className="register-submit-btn">
            Submit Registration
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegisterModal;
