import React, { useRef, useEffect, useState } from "react";

// Use Tailwind CSS for a clean, modern look and responsive design
// Ensure you have Tailwind configured in your project
// Or, for a self-contained component, you can add this script tag to your HTML
// <script src="https://cdn.tailwindcss.com"></script>

const opportunitiesData = [
  {
    title: "Fashion & Jewellery Franchise",
    description: [
      "Clothing brands for Men, kids and women",
      "Gold, silver, diamond, Jewellery Brands",
      "Imitation/Art Jewellery",
      "Home décor & furniture brands",
    ],
    img: "https://placehold.co/400x300/F5C87E/white?text=Fashion",
  },
  {
    title: "Food and Restaurant",
    description: [
      "Food & beverages brands/FMCG brands",
      "Restaurant, QSR’s, bars and lounge franchise",
      "Institutional suppliers, technology co’s/ restaurant apps",
      "Food delivery co’s & vehicles",
    ],
    img: "https://placehold.co/400x300/98C1D9/black?text=Food",
  },
  {
    title: "Hotel, Travel & Tourism & Entertainment",
    description: [
      "Successful travel companies",
      "Online travel services provider",
      "Hospitality business franchise",
      "Big Travel Agencies",
    ],
    img: "https://placehold.co/400x300/4F6D7A/white?text=Hotel",
  },
  {
    title: "Dealer Distribution & Business Opportunities",
    description: [
      "Education Products",
      "Electronic and Electrical",
      "Pharmaceuticals & Medical Supplies",
      "Security and Protection",
      "Building and Construction",
    ],
    img: "https://placehold.co/400x300/C15F63/white?text=Dealer",
  },
  {
    title: "Education & Career",
    description: [
      "Schools, Coaching Institutes, Preschools",
      "Online Education Providers",
      "Student Placement and Counseling Services",
      "Career Guidance and Training Centers",
    ],
    img: "https://placehold.co/400x300/84A98C/white?text=Education",
  },
  {
    title: "Health & Wellness",
    description: [
      "Gym and Fitness Centers",
      "Spa and Salon Franchises",
      "Diet and Nutrition Clinics",
      "Yoga and Meditation Centers",
    ],
    img: "https://placehold.co/400x300/52796F/white?text=Health",
  },
];

const OpportunitiesSection = () => {
  const scrollRef = useRef(null);
  const scrollIntervalRef = useRef(null);

  // Function to start the automatic scroll
  const startAutoScroll = () => {
    // Clear any existing interval to prevent multiple instances
    clearInterval(scrollIntervalRef.current);

    scrollIntervalRef.current = setInterval(() => {
      if (scrollRef.current) {
        // Calculate the maximum scrollable width
        const maxScrollLeft = scrollRef.current.scrollWidth - scrollRef.current.clientWidth;
        
        // Check if we are at the end of the scrollable area
        if (scrollRef.current.scrollLeft >= maxScrollLeft) {
          // If at the end, smoothly scroll back to the beginning
          scrollRef.current.scrollTo({
            left: 0,
            behavior: "smooth",
          });
        } else {
          // Otherwise, scroll a fixed amount to the right
          scrollRef.current.scrollBy({
            left: 300,
            behavior: "smooth",
          });
        }
      }
    }, 3000); // Scrolls every 3 seconds
  };

  // Function to stop the automatic scroll
  const stopAutoScroll = () => {
    clearInterval(scrollIntervalRef.current);
  };

  useEffect(() => {
    // Start the auto-scroll when the component mounts
    startAutoScroll();

    // Cleanup function to stop the scroll when the component unmounts
    return () => stopAutoScroll();
  }, []); // Empty dependency array ensures this runs only once on mount

  return (
    <section className="bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto text-center">
        <h2 className="text-3xl font-extrabold text-gray-900 sm:text-4xl mb-8">
          Opportunities at Franxpo
        </h2>
        <div className="relative">
          {/* Scrollable container for the cards */}
          <div
            className="flex gap-6 overflow-x-auto p-4 scrollbar-hide"
            ref={scrollRef}
            // Pause auto-scroll on hover
            onMouseEnter={stopAutoScroll}
            // Resume auto-scroll on mouse leave
            onMouseLeave={startAutoScroll}
          >
            {opportunitiesData.map((item, index) => (
              <div
                className="flex-shrink-0 w-80 bg-white rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-300 overflow-hidden"
                key={index}
              >
                <img
                  src={item.img}
                  alt={item.title}
                  className="w-full h-48 object-cover rounded-t-xl"
                  onerror="this.onerror=null;this.src='https://placehold.co/400x300/4F6D7A/white?text=Placeholder';"
                />
                <div className="p-6">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">{item.title}</h3>
                  <ul className="text-sm text-gray-600 space-y-1 text-left list-disc pl-5">
                    {item.description.map((desc, i) => (
                      <li key={i}>{desc}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default OpportunitiesSection;
