import React, { useEffect, useRef, useState } from "react";
import "./OurPartners.css";
import venueLogo from "../assets/logo/venue_partner.jpeg";
import franmaxLogo from "../assets/logo/franmax_logo.png";

const OurPartners = () => {
  const partners = [
    { name: "Radio FM Partner", logo: venueLogo }, // change logo later
    { name: "Venue Partner", logo: venueLogo },
    { name: "Franchise Partner", logo: franmaxLogo },
    { name: "Digital Media Partner", logo: venueLogo }, // change logo later
    { name: "Leasing Partner", logo: venueLogo }, // change logo later
  ];

  // Duplicate the list to allow seamless looping
  const [currentIndex, setCurrentIndex] = useState(0);
  const containerRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) =>
        prev === partners.length - 1 ? 0 : prev + 1
      );
    }, 3000);

    return () => clearInterval(interval);
  }, [partners.length]);

  return (
    <section className="partners-section">
      <div className="partners-container">
        <h2 className="partners-title">Our Partners</h2>
        <p className="partners-subtitle">
          Weâ€™re proud to collaborate with leading brands who make this event possible.
        </p>

        <div className="carousel-wrapper">
          <div
            className="carousel-inner"
            ref={containerRef}
            style={{
              transform: `translateX(-${currentIndex * 220}px)`, // card width + gap
              transition: "transform 0.6s ease-in-out",
            }}
          >
            {partners.concat(partners[0]).map((partner, index) => (
              <div className="partner-card" key={index}>
                <div className="partner-logo">
                  <img src={partner.logo} alt={partner.name} />
                </div>
                <h3 className="partner-name">{partner.name}</h3>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default OurPartners;
