import React, { useState } from "react";
import {
  Rocket,
  Award,
  Gem,
  Mail,
  Home,
  Info,
  Briefcase,
  Menu,
  X,
} from "lucide-react";

/** Reusable NavLink */
const NavLink = ({ href, children, Icon, onClick }) => (
  <a
    href={href}
    onClick={onClick}
    className="flex items-center space-x-2 text-gray-600 hover:text-indigo-600 transition-colors duration-200 font-medium"
  >
    {Icon && <Icon size={20} />}
    <span>{children}</span>
  </a>
);

/** Header with responsive menu */
function Header() {
  const [mobileOpen, setMobileOpen] = useState(false);

  const toggleMenu = () => setMobileOpen(!mobileOpen);
  const closeMenu = () => setMobileOpen(false);

  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-white/80 backdrop-blur-md shadow-sm">
      <nav className="container mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
        {/* Brand */}
        <div className="flex-shrink-0">
          <NavLink href="#" Icon={Home}>OnePager</NavLink>
        </div>

        {/* Desktop Nav */}
        <div className="hidden md:flex items-center space-x-8">
          <NavLink href="#about" Icon={Info}>About</NavLink>
          <NavLink href="#services" Icon={Briefcase}>Services</NavLink>
          <NavLink href="#contact" Icon={Mail}>Contact</NavLink>
        </div>

        {/* Mobile Menu Button */}
        <div className="md:hidden">
          <button
            onClick={toggleMenu}
            className="text-gray-600 hover:text-indigo-600 focus:outline-none"
          >
            {mobileOpen ? <X size={28} /> : <Menu size={28} />}
          </button>
        </div>
      </nav>

      {/* Mobile Dropdown */}
      {mobileOpen && (
        <div className="md:hidden bg-white shadow-md">
          <div className="flex flex-col p-4 space-y-4">
            <NavLink href="#about" Icon={Info} onClick={closeMenu}>About</NavLink>
            <NavLink href="#services" Icon={Briefcase} onClick={closeMenu}>Services</NavLink>
            <NavLink href="#contact" Icon={Mail} onClick={closeMenu}>Contact</NavLink>
          </div>
        </div>
      )}
    </header>
  );
}

/** Hero section */
function Hero() {
  return (
    <section className="bg-gradient-to-br from-indigo-500 to-indigo-700 text-white py-24 md:py-32 flex items-center min-h-screen">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold mb-4 tracking-tight leading-tight">
          Your Success, Our Mission
        </h1>
        <p className="text-lg md:text-xl text-indigo-100 mb-8 max-w-2xl mx-auto">
          We help you build powerful and elegant web experiences. Let's create something amazing together.
        </p>
        <button className="bg-white text-indigo-600 font-semibold py-3 px-8 rounded-full shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
          Get Started
        </button>
      </div>
    </section>
  );
}

/** About section */
function About() {
  return (
    <section id="about" className="py-20 md:py-24 bg-white">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 text-gray-800">
          Who We Are
        </h2>
        <div className="max-w-4xl mx-auto text-center">
          <p className="text-lg text-gray-600 leading-relaxed">
            We are a team of passionate developers and designers dedicated to creating high-quality, user-centric digital solutions. From small business websites to large-scale enterprise applications, we leverage the latest technologies to bring your ideas to life with precision and creativity.
          </p>
        </div>
      </div>
    </section>
  );
}

/** Services section */
function Services() {
  return (
    <section id="services" className="bg-gray-100 py-20 md:py-24">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <h2 className="text-3xl md:text-4xl font-bold text-center mb-12 text-gray-800">
          Our Services
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-5xl mx-auto">
          <ServiceCard
            icon={<Rocket className="h-10 w-10 text-indigo-500" />}
            title="Web Development"
            description="Building fast, secure, and scalable websites tailored to your business needs."
          />
          <ServiceCard
            icon={<Gem className="h-10 w-10 text-indigo-500" />}
            title="UI/UX Design"
            description="Crafting intuitive and engaging user interfaces that delight your customers."
          />
          <ServiceCard
            icon={<Award className="h-10 w-10 text-indigo-500" />}
            title="Digital Strategy"
            description="Developing comprehensive digital plans to help you achieve your online goals."
          />
        </div>
      </div>
    </section>
  );
}

/** Reusable Service Card */
const ServiceCard = ({ icon, title, description }) => (
  <div className="flex flex-col items-center text-center p-8 bg-white rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-300 border border-gray-200">
    <div className="p-4 rounded-full bg-indigo-100 mb-4">{icon}</div>
    <h3 className="text-xl font-semibold mb-2 text-gray-800">{title}</h3>
    <p className="text-gray-600">{description}</p>
  </div>
);

/** Footer section */
function Footer() {
  return (
    <footer id="contact" className="bg-gray-800 text-gray-300 py-12">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <h3 className="text-2xl font-semibold text-white mb-4">Get In Touch</h3>
        <p className="text-gray-400 mb-8 max-w-xl mx-auto">
          Ready to start your project? We're here to help. Reach out to us for a free consultation.
        </p>
        <a
          href="mailto:info@onepager.com"
          className="inline-flex items-center bg-indigo-600 text-white font-medium py-3 px-6 rounded-full shadow-md hover:bg-indigo-700 transition-colors duration-200"
        >
          <Mail size={20} className="mr-2" />
          info@onepager.com
        </a>
      </div>
      <div className="mt-8 pt-6 border-t border-gray-700 text-center">
        <p className="text-sm text-gray-500">
          &copy; 2024 OnePager. All rights reserved.
        </p>
      </div>
    </footer>
  );
}

/** Main App */
export default function App() {
  return (
    <div className="bg-gray-50 font-sans text-gray-900 antialiased">
      <Header />
      <main className="pt-24">
        <Hero />
        <About />
        <Services />
      </main>
      <Footer />
    </div>
  );
}
