import React, { useState, useEffect } from "react";
import Select from "react-select";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./BookingModal.css"; // use same CSS
import logo from "../assets/logo/logo.png";
import { getApiUrl } from "../utils/api";

const RegisterModal = ({ onClose }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    contact: "",
    state: null,
    city: null,
  });
  const [states, setStates] = useState([]);
  const [cities, setCities] = useState([]);
  const [loadingStates, setLoadingStates] = useState(false);
  const [loadingCities, setLoadingCities] = useState(false);
  const [termsAccepted, setTermsAccepted] = useState(false);
  const [termsModalOpen, setTermsModalOpen] = useState(false);

  // Lock background scroll
  useEffect(() => {
    document.body.style.overflow = "hidden";
    return () => (document.body.style.overflow = "");
  }, []);

  useEffect(() => {
    const fetchStates = async () => {
      setLoadingStates(true);
      try {
        const res = await fetch(getApiUrl("get-indian-states.php"));
        const data = await res.json();
        setStates(data.map((s) => ({ value: s.id, label: s.name })));
      } catch (err) {
        toast.error("Error fetching states");
      } finally {
        setLoadingStates(false);
      }
    };
    fetchStates();
  }, []);

  useEffect(() => {
    const fetchCities = async () => {
      if (!formData.state) return setCities([]);
      setLoadingCities(true);
      try {
        const res = await fetch(
          getApiUrl(`get-cities.php?state_id=${formData.state.value}`)
        );
        const data = await res.json();
        setCities(data.map((c) => ({ value: c.id, label: c.name })));
      } catch (err) {
        toast.error("Error fetching cities");
      } finally {
        setLoadingCities(false);
      }
    };
    fetchCities();
  }, [formData.state]);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleStateChange = (selected) =>
    setFormData({ ...formData, state: selected, city: null });
  const handleCityChange = (selected) =>
    setFormData({ ...formData, city: selected });

  const validateForm = () => {
    if (!formData.name.trim()) return "Name is required";
    if (!formData.email.trim()) return "Email is required";
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) return "Invalid email";
    if (!formData.contact.trim()) return "Contact is required";
    const contactRegex = /^[6-9][0-9]{9}$/;
    if (!contactRegex.test(formData.contact))
      return "Contact must start with 6,7,8,9 and 10 digits";
    if (!formData.state) return "Please select a state";
    if (!formData.city) return "Please select a city";
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!termsAccepted) {
      toast.error("Please accept Terms & Conditions");
      return;
    }
    const error = validateForm();
    if (error) return toast.error(error);

    const payload = {
      name: formData.name.trim(),
      email: formData.email.trim(),
      contact: formData.contact.trim(),
      state_id: formData.state.value,
      city_id: formData.city.value,
    };

    try {
      const res = await fetch(getApiUrl("register-user.php"), {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const data = await res.json();
      if (data.success) {
        toast.success("Registration successful!");
        onClose();
      } else toast.error(data.message || "Registration failed");
    } catch (err) {
      console.error(err);
      toast.error("Server error. Try again later.");
    }
  };

  const termsText = [
    "Registration is subject to approval.",
    "Payment must be completed for confirmation.",
    "No refund after payment.",
    "Franmax India reserves the right to modify terms.",
  ];

  return (
    <>
      <div className="modal-overlay">
        <div className="modal">
          <button className="close-btn" onClick={onClose}>
            &times;
          </button>

          <div className="modal-header">
            <img src={logo} alt="Franmax Expo Logo" className="company-logo" />
            <h2>Register for Franmax Expo 2025</h2>
            <p className="subtitle">Secure your registration now</p>
          </div>

          <div className="benefits-section">
            <h3>Why Attend?</h3>
            <ul>
              <li>Discover top franchise opportunities</li>
              <li>Meet investors and industry experts</li>
              <li>Learn from keynote speakers & workshops</li>
              <li>Network with decision makers</li>
            </ul>
          </div>

          <form onSubmit={handleSubmit} className="booking-form">
            <div className="form-row">
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={formData.name}
                onChange={handleChange}
                required
              />
              <input
                type="email"
                name="email"
                placeholder="Email Address"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <input
                type="tel"
                name="contact"
                placeholder="Contact Number"
                value={formData.contact}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-row">
              <Select
                options={states}
                value={formData.state}
                onChange={handleStateChange}
                isLoading={loadingStates}
                placeholder="Select State"
              />
              <Select
                options={cities}
                value={formData.city}
                onChange={handleCityChange}
                isLoading={loadingCities}
                placeholder="Select City"
                isDisabled={!formData.state}
              />
            </div>

            <div className="form-row terms">
              <label>
                <input
                  type="checkbox"
                  checked={termsAccepted}
                  onChange={(e) => setTermsAccepted(e.target.checked)}
                />
                I agree to the{" "}
                <span
                  className="terms-link"
                  onClick={() => setTermsModalOpen(true)}
                  style={{
                    color: "#ff6b00",
                    cursor: "pointer",
                    textDecoration: "underline",
                  }}
                >
                  Terms and Conditions
                </span>
              </label>
            </div>

            <button
              type="submit"
              className="submit-btn"
              disabled={!termsAccepted}
            >
              Submit
            </button>
          </form>
        </div>
      </div>

      {termsModalOpen && (
        <div className="modal-overlay">
          <div className="modal terms-modal">
            <button
              className="close-btn"
              onClick={() => setTermsModalOpen(false)}
            >
              &times;
            </button>
            <h2>Terms & Conditions</h2>
            <div className="terms-content">
              <ul>
                {termsText.map((item, idx) => (
                  <li key={idx}>{item}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      )}

      <ToastContainer
        position="top-left"
        autoClose={3000}
        hideProgressBar
        newestOnTop
        closeOnClick
        pauseOnHover
        draggable
      />
    </>
  );
};

export default RegisterModal;
