import React, { useState, useEffect, useRef } from "react";
import './HomePage.css';

import Banner from './Banner';
import AboutSection from './AboutSection';
import ExclusiveRights from "./ExclusiveRights";
import ContactSection from "./ContactSection";
import OurPartners from "./OurPartners";
import CountdownTimer from "./CountdownTimer";

const sectionVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8, ease: "easeOut" } }
};

const Homepage = () => {
  const iconRef = useRef(null);
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 20, y: window.innerHeight - 160 });
  const dragOffset = useRef({ x: 0, y: 0 });

  // Drag start
  const startDrag = (clientX, clientY) => {
    setIsDragging(true);
    const rect = iconRef.current.getBoundingClientRect();
    dragOffset.current = { x: clientX - rect.left, y: clientY - rect.top };
  };

  // Drag move
  const doDrag = (clientX, clientY) => {
    if (!isDragging) return;

    const iconWidth = iconRef.current.offsetWidth;
    const iconHeight = iconRef.current.offsetHeight;

    const newX = clientX - dragOffset.current.x;
    const newY = clientY - dragOffset.current.y;

    setPosition({
      x: Math.max(0, Math.min(newX, window.innerWidth - iconWidth)),
      y: Math.max(0, Math.min(newY, window.innerHeight - iconHeight)),
    });
  };

  // Drag stop
  const stopDrag = () => setIsDragging(false);

  // Mouse events
  useEffect(() => {
    const handleMouseMove = (e) => doDrag(e.clientX, e.clientY);
    const handleMouseUp = stopDrag;

    if (isDragging) {
      window.addEventListener("mousemove", handleMouseMove);
      window.addEventListener("mouseup", handleMouseUp);
    }

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("mouseup", handleMouseUp);
    };
  }, [isDragging]);

  // Touch events
  useEffect(() => {
    const handleTouchMove = (e) => doDrag(e.touches[0].clientX, e.touches[0].clientY);
    const handleTouchEnd = stopDrag;

    if (isDragging) {
      window.addEventListener("touchmove", handleTouchMove);
      window.addEventListener("touchend", handleTouchEnd);
    }

    return () => {
      window.removeEventListener("touchmove", handleTouchMove);
      window.removeEventListener("touchend", handleTouchEnd);
    };
  }, [isDragging]);

  // Click only if not dragging
  const handleClick = () => {
    if (!isDragging) window.open("https://franmaxindia.com", "_blank");
  };

  return (
    <div className="homepage">
      <main className="main-content">
        <Banner />
        <AboutSection />
        <ExclusiveRights />
        <CountdownTimer />
        <OurPartners />
        <ContactSection />
      </main>

      {/* ================= Draggable Icon ================= */}
      <img
        ref={iconRef}
        src={require('../assets/9e5d6c19234ec1aeeba6b782e7c31073.jpg')}
        alt="Draggable Link"
        onMouseDown={(e) => startDrag(e.clientX, e.clientY)}
        onTouchStart={(e) => startDrag(e.touches[0].clientX, e.touches[0].clientY)}
        onClick={handleClick}
        style={{
          position: "fixed",
          left: position.x,
          top: position.y,
          width: 60,
          height: 60,
          borderRadius: "50%",
          cursor: isDragging ? "grabbing" : "grab",
          zIndex: 9999,
          transition: isDragging ? "none" : "transform 0.1s",
        }}
      />
    </div>
  );
};

export default Homepage;
