import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

const DraggableIcon = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [windowSize, setWindowSize] = useState({ width: window.innerWidth, height: window.innerHeight });

  // Update window size on resize
  useEffect(() => {
    const handleResize = () => {
      setWindowSize({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const handleClick = () => {
    if (!isDragging) {
      window.open("https://franmaxindia.com", "_blank");
    }
  };

  // Clamp position so it stays inside viewport
  const clampPosition = (value, max) => Math.min(Math.max(value, 0), max);

  return (
    <motion.img
      src={require('../assets/9e5d6c19234ec1aeeba6b782e7c31073.jpg')}
      alt="Draggable Link"
      drag
      dragMomentum={true}
      dragElastic={0.2}
      className="floating-icon"
      onDragStart={() => setIsDragging(true)}
      onDragEnd={() => setTimeout(() => setIsDragging(false), 50)}
      onClick={handleClick}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      style={{
        cursor: 'grab',
        width: 60,
        height: 60,
        borderRadius: '10%',
        position: 'fixed',
        top: windowSize.height / 2 - 30, // start center of screen
        left: windowSize.width / 2 - 30,
      }}
      dragConstraints={{
        top: 0,
        left: 0,
        right: windowSize.width - 60,
        bottom: windowSize.height - 60,
      }}
    />
  );
};

export default DraggableIcon;
