import React, { useState, useEffect } from "react";
import "./Banner.css";
import ActionModal from "./ActionModal";
import BannerImage from '../assets/logo/banner.png';

const Banner = () => {
  // Use direct image paths (strings), not objects
  const images = [
    BannerImage,
    BannerImage
  ];

  const bannerTexts = [
    {
      title: "Where Ambitious Brands Meet Smart Investors",
      subtitle: "Unlock partnerships and growth opportunities",
      button: "Book Your Stall"
    },
    // {
    //   title: "Expand Your Network, Grow Your Franchise",
    //   subtitle: "Connect with industry leaders and partners",
    //   button: "Register Now"
    // }
  ];

  const [currentIndex, setCurrentIndex] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex(prevIndex =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, 4000);
    return () => clearInterval(interval);
  }, [images.length]);

  const { title, subtitle, button } = bannerTexts[currentIndex];

  const handleButtonClick = () => {
    setIsModalOpen(true);
  };

  return (
    <>
      <div
        id="home"
        className="banner"
        style={{ backgroundImage: `url(${images[currentIndex]})` }}
      >
        <div className="overlay">
          <h1>{title}</h1>
          <p>{subtitle}</p>
          <button className="banner-btn" onClick={handleButtonClick}>
            {button}
          </button>
        </div>
      </div>

      {isModalOpen && <ActionModal onClose={() => setIsModalOpen(false)} />}
    </>
  );
};

export default Banner;
