import React, { useEffect, useState, useRef } from "react";
import "./CountdownTimer.css";

const CountdownTimer = () => {
  // Countdown calculation
  const calculateTimeLeft = () => {
    const targetDate = new Date("2025-09-13T00:00:00");
    const now = new Date();
    const difference = targetDate - now;

    if (difference <= 0) return { expired: true };

    return {
      days: Math.floor(difference / (1000 * 60 * 60 * 24)),
      hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((difference / 1000 / 60) % 60),
      seconds: Math.floor((difference / 1000) % 60),
    };
  };

  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

  // Draggable state
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({
    x: 20,
    y: window.innerHeight - 160,
  });
  const dragOffset = useRef({ x: 0, y: 0 });
  const boxRef = useRef(null);

  // Timer effect
  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // Mouse & touch handlers
  const startDrag = (clientX, clientY) => {
    setIsDragging(true);
    const rect = boxRef.current.getBoundingClientRect();
    dragOffset.current = {
      x: clientX - rect.left,
      y: clientY - rect.top,
    };
  };

  const doDrag = (clientX, clientY) => {
    if (!isDragging) return;
    const boxWidth = boxRef.current.offsetWidth;
    const boxHeight = boxRef.current.offsetHeight;
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;

    const newX = clientX - dragOffset.current.x;
    const newY = clientY - dragOffset.current.y;

    setPosition({
      x: Math.max(0, Math.min(newX, viewportWidth - boxWidth)),
      y: Math.max(0, Math.min(newY, viewportHeight - boxHeight)),
    });
  };

  const stopDrag = () => setIsDragging(false);

  // Attach mouse events
  useEffect(() => {
    const handleMouseMove = (e) => doDrag(e.clientX, e.clientY);
    const handleMouseUp = stopDrag;

    if (isDragging) {
      window.addEventListener("mousemove", handleMouseMove);
      window.addEventListener("mouseup", handleMouseUp);
    }
    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("mouseup", handleMouseUp);
    };
  }, [isDragging]);

  // Attach touch events
  useEffect(() => {
    const handleTouchMove = (e) => {
      const touch = e.touches[0];
      doDrag(touch.clientX, touch.clientY);
    };
    const handleTouchEnd = stopDrag;

    if (isDragging) {
      window.addEventListener("touchmove", handleTouchMove);
      window.addEventListener("touchend", handleTouchEnd);
    }
    return () => {
      window.removeEventListener("touchmove", handleTouchMove);
      window.removeEventListener("touchend", handleTouchEnd);
    };
  }, [isDragging]);

  // Calendar link generator
  const getGoogleCalendarLink = () => {
    const event = {
      title: "Franmax Event 2025",
      startDate: "20250914T000000Z",
      endDate: "20250914T235900Z",
      details: "Join us for the exciting Franmax Event 2025!",
      location:
        "Taj Skyline Ahmedabad, Sankalp Square III, Opp. Saket 3, Sindhubhavan Road, Shilaj, Gujarat 380059",
    };
    const baseUrl =
      "https://calendar.google.com/calendar/render?action=TEMPLATE";
    return `${baseUrl}&text=${encodeURIComponent(
      event.title
    )}&dates=${event.startDate}/${event.endDate}&details=${encodeURIComponent(
      event.details
    )}&location=${encodeURIComponent(event.location)}`;
  };

  return (
    <div
      ref={boxRef}
      className={`countdown-wrapper ${isDragging ? "dragging" : ""}`}
      style={{
        position: "fixed",
        left: `${position.x}px`,
        top: `${position.y}px`,
      }}
    >
      {/* Draggable Header / Handle */}
      <div
        className="countdown-header"
        onMouseDown={(e) => startDrag(e.clientX, e.clientY)}
        onTouchStart={(e) => {
          const touch = e.touches[0];
          startDrag(touch.clientX, touch.clientY);
        }}
      >
       
      </div>

      {/* Actual Countdown Box */}
      <div className="countdown-box">
        {timeLeft.expired ? (
          <>
            <h3>Event Started!</h3>
            <a
              href={getGoogleCalendarLink()}
              target="_blank"
              rel="noopener noreferrer"
              className="add-calendar-button"
            >
              Add to Google Calendar
            </a>
          </>
        ) : (
          <>
            <h3>Countdown to Event</h3>
            <div className="countdown-timer">
              {["days", "hours", "minutes", "seconds"].map((unit) => (
                <div key={unit} className="time-segment">
                  <span>{String(timeLeft[unit]).padStart(2, "0")}</span>
                  <small>
                    {unit.charAt(0).toUpperCase() + unit.slice(1)}
                  </small>
                </div>
              ))}
            </div>
            <a
              href={getGoogleCalendarLink()}
              target="_blank"
              rel="noopener noreferrer"
              className="add-calendar-button"
            >
              Add to Google Calendar
            </a>
          </>
        )}
      </div>
    </div>
  );
};

export default CountdownTimer;
