import React, { useState, useEffect } from "react";
import Select from "react-select";
import "./BookingModal.css";
import logo from "../assets/logo/logo.png";

const BookingModal = ({ onClose }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    contact: "",
    state: null,   // will store object {value,label}
    city: null,    // will store object {value,label}
    description: ""
  });

  const [states, setStates] = useState([]);
  const [cities, setCities] = useState([]);
  const [loadingStates, setLoadingStates] = useState(false);
  const [loadingCities, setLoadingCities] = useState(false);

  // Fetch states on mount
  useEffect(() => {
    const fetchStates = async () => {
      setLoadingStates(true);
      try {
        const res = await fetch("http://localhost/react-api/get-indian-states.php"); // <-- replace with your API
        const data = await res.json();
        const formatted = data.map(item => ({
          value: item.id,
          label: item.name
        }));
        setStates(formatted);
      } catch (err) {
        console.error("Error fetching states:", err);
      } finally {
        setLoadingStates(false);
      }
    };
    fetchStates();
  }, []);

  // Fetch cities whenever a state is selected
  useEffect(() => {
    const fetchCities = async () => {
      if (!formData.state) {
        setCities([]);
        return;
      }
      setLoadingCities(true);
      try {
        const res = await fetch(`http://localhost/react-api/get-cities.php?state_id=${formData.state.value}`); // <-- replace with your API
        const data = await res.json();
        const formatted = data.map(item => ({
          value: item.id,
          label: item.name
        }));
        setCities(formatted);
      } catch (err) {
        console.error("Error fetching cities:", err);
      } finally {
        setLoadingCities(false);
      }
    };
    fetchCities();
  }, [formData.state]);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleStateChange = selected => {
    setFormData({ ...formData, state: selected, city: null }); // reset city when state changes
  };

  const handleCityChange = selected => {
    setFormData({ ...formData, city: selected });
  };

  const handleSubmit = e => {
    e.preventDefault();
    console.log("Form submitted:", formData);
    alert("Your request has been submitted!");
    onClose();
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="close-btn" onClick={onClose}>
          &times;
        </button>

        {/* Company Logo */}
        <div className="modal-header">
          <img src={logo} alt="Franmax Expo Logo" className="company-logo" />
          <h2>Book Your Stall</h2>
          <p className="subtitle">Secure your spot at Franmax Expo 2025</p>
        </div>

        {/* Why Book Section */}
        <div className="benefits-section">
          <h3>Why Exhibit With Us?</h3>
          <ul>
            <li>Connect with top investors and franchise owners</li>
            <li>Showcase your brand to 500+ visitors</li>
            <li>Expand your business across India</li>
            <li>Get exclusive media coverage & leads</li>
          </ul>
        </div>

        {/* Booking Form */}
        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-row">
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={formData.name}
              onChange={handleChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-row">
            <input
              type="tel"
              name="contact"
              placeholder="Contact Number"
              value={formData.contact}
              onChange={handleChange}
              required
            />
            <textarea
              name="description"
              placeholder="Description"
              rows="2"
              value={formData.description}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-row">
            <Select
              options={states}
              value={formData.state}
              onChange={handleStateChange}
              isLoading={loadingStates}
              placeholder="Select State"
              isSearchable
              required
            />
            <Select
              options={cities}
              value={formData.city}
              onChange={handleCityChange}
              isLoading={loadingCities}
              placeholder="Select City"
              isSearchable
              isDisabled={!formData.state}
              required
            />
          </div>

          
         
          <button type="submit" className="submit-btn">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingModal;
