import React, { useState, useEffect } from "react";
import Select from "react-select";
import "./BookingModal.css";
import logo from "../assets/logo/logo.png";

const BookingModal = ({ onClose }) => {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        contact: "",
        state: null,
        city: null,
        description: "",
        sponsorship: null, // new field for sponsorship
    });

    const [states, setStates] = useState([]);
    const [cities, setCities] = useState([]);
    const [loadingStates, setLoadingStates] = useState(false);
    const [loadingCities, setLoadingCities] = useState(false);

    // Fetch states
    useEffect(() => {
        const fetchStates = async () => {
            setLoadingStates(true);
            try {
                const res = await fetch("http://localhost/react-api/get-indian-states.php");
                const data = await res.json();
                const formatted = data.map((item) => ({
                    value: item.id,
                    label: item.name,
                }));
                setStates(formatted);
            } catch (err) {
                console.error("Error fetching states:", err);
            } finally {
                setLoadingStates(false);
            }
        };
        fetchStates();
    }, []);

    // Fetch cities based on state
    useEffect(() => {
        const fetchCities = async () => {
            if (!formData.state) {
                setCities([]);
                return;
            }
            setLoadingCities(true);
            try {
                const res = await fetch(
                    `http://localhost/react-api/get-cities.php?state_id=${formData.state.value}`
                );
                const data = await res.json();
                const formatted = data.map((item) => ({
                    value: item.id,
                    label: item.name,
                }));
                setCities(formatted);
            } catch (err) {
                console.error("Error fetching cities:", err);
            } finally {
                setLoadingCities(false);
            }
        };
        fetchCities();
    }, [formData.state]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleStateChange = (selected) => {
        setFormData({ ...formData, state: selected, city: null });
    };

    const handleCityChange = (selected) => {
        setFormData({ ...formData, city: selected });
    };

    const handleSponsorshipChange = (selected) => {
        setFormData({ ...formData, sponsorship: selected });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Prepare payload
        const payload = {
            name: formData.name,
            email: formData.email,
            contact: formData.contact,
            state_id: formData.state?.value || "",
            city_id: formData.city?.value || "",
            description: formData.description,
            sponsorship: formData.sponsorship?.value || "",
        };
        console.log(payload);
        try {
            const response = await fetch("http://localhost/react-api/submit-booking.php", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(payload),
            });

            const data = await response.json();

            if (data.success) {
                alert("Your request has been submitted!");
                onClose();
            } else {
                alert("Error: " + (data.message || "Something went wrong"));
            }
        } catch (err) {
            console.error("Error submitting form:", err);
            alert("Failed to submit. Please try again.");
        }
    };


    const sponsorshipOptions = [
        { value: "yes", label: "Yes" },
        { value: "no", label: "No" },
    ];

    return (
        <div className="modal-overlay">
            <div className="modal">
                <button className="close-btn" onClick={onClose}>
                    &times;
                </button>

                <div className="modal-header">
                    <img src={logo} alt="Franmax Expo Logo" className="company-logo" />
                    <h2>Book Your Stall</h2>
                    <p className="subtitle">Secure your spot at Franmax Expo 2025</p>
                </div>

                <div className="benefits-section">
                    <h3>Why Exhibit With Us?</h3>
                    <ul>
                        <li>Connect with top investors and franchise owners</li>
                        <li>Showcase your brand to 500+ visitors</li>
                        <li>Expand your business across India</li>
                        <li>Get exclusive media coverage & leads</li>
                    </ul>
                </div>

                <form onSubmit={handleSubmit} className="booking-form">
                    <div className="form-row">
                        <input
                            type="text"
                            name="name"
                            placeholder="Name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                        <input
                            type="email"
                            name="email"
                            placeholder="Email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="form-row">
                        <input
                            type="tel"
                            name="contact"
                            placeholder="Contact Number"
                            value={formData.contact}
                            onChange={handleChange}
                            required
                        />
                        <textarea
                            name="description"
                            placeholder="Description"
                            rows="2"
                            value={formData.description}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="form-row">
                        <Select
                            options={states}
                            value={formData.state}
                            onChange={handleStateChange}
                            isLoading={loadingStates}
                            placeholder="Select State"
                            isSearchable
                            required
                        />
                        <Select
                            options={cities}
                            value={formData.city}
                            onChange={handleCityChange}
                            isLoading={loadingCities}
                            placeholder="Select City"
                            isSearchable
                            isDisabled={!formData.state}
                            required
                        />
                    </div>

                    {/* Sponsorship Field */}
                    <div className="form-row">
                        <Select
                            options={sponsorshipOptions}
                            value={formData.sponsorship}
                            onChange={handleSponsorshipChange}
                            placeholder="Interested in Sponsorship?"
                            isSearchable={false}
                            required
                        />
                    </div>

                    <button type="submit" className="submit-btn">
                        Submit
                    </button>
                </form>
            </div>
        </div>
    );
};

export default BookingModal;
