import React, { useState, useEffect, useRef } from "react";

const App = () => {
  const [isModalOpen, setIsModalOpen] = useState(true);

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <button
        onClick={openModal}
        className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition duration-300"
      >
        Open Booking Form
      </button>
      {isModalOpen && <BookingModal onClose={closeModal} />}
    </div>
  );
};

const BookingModal = ({ onClose }) => {
  // Use state to manage form data
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    contact: "",
    state: "", // storing a string value now
    city: "",  // storing a string value now
    description: "",
  });

  // Use state to manage validation errors
  const [errors, setErrors] = useState({});

  // Use state to manage the success/error message after submission
  const [submitMessage, setSubmitMessage] = useState({
    message: "",
    isError: false,
  });

  const [states, setStates] = useState([]);
  const [cities, setCities] = useState([]);
  const [loadingStates, setLoadingStates] = useState(false);
  const [loadingCities, setLoadingCities] = useState(false);

  // Use a ref to manage the modal's internal scroll
  const modalRef = useRef(null);

  // Fetch states on mount
  useEffect(() => {
    const fetchStates = async () => {
      setLoadingStates(true);
      try {
        // Simulating an API call with a hardcoded list of states for a runnable demo
        const demoStates = [
          { value: "1", label: "Gujarat" },
          { value: "2", label: "Maharashtra" },
          { value: "3", label: "Delhi" },
        ];
        // Use a timeout to simulate network latency
        await new Promise(resolve => setTimeout(resolve, 500));
        setStates(demoStates);
      } catch (err) {
        console.error("Error fetching states:", err);
      } finally {
        setLoadingStates(false);
      }
    };
    fetchStates();
  }, []);

  // Fetch cities whenever a state is selected
  useEffect(() => {
    const fetchCities = async () => {
      if (!formData.state) {
        setCities([]);
        return;
      }
      setLoadingCities(true);
      try {
        // Simulating an API call for cities based on a state ID
        let demoCities = [];
        if (formData.state === "1") {
          demoCities = [
            { value: "101", label: "Ahmedabad" },
            { value: "102", label: "Surat" },
          ];
        } else if (formData.state === "2") {
          demoCities = [
            { value: "201", label: "Mumbai" },
            { value: "202", label: "Pune" },
          ];
        } else if (formData.state === "3") {
          demoCities = [
            { value: "301", label: "New Delhi" },
          ];
        }
        await new Promise(resolve => setTimeout(resolve, 500));
        setCities(demoCities);
      } catch (err) {
        console.error("Error fetching cities:", err);
      } finally {
        setLoadingCities(false);
      }
    };
    fetchCities();
  }, [formData.state]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    // Clear the error for the field as the user types
    if (errors[name]) {
      setErrors({ ...errors, [name]: "" });
    }
  };
  
  const validateForm = () => {
    let isValid = true;
    let newErrors = {};

    // Name validation
    if (!formData.name.trim()) {
      newErrors.name = "Name is required.";
      isValid = false;
    }

    // Email validation
    if (!formData.email.trim()) {
      newErrors.email = "Email is required.";
      isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "Email is not valid.";
      isValid = false;
    }

    // Contact validation
    if (!formData.contact.trim()) {
      newErrors.contact = "Contact number is required.";
      isValid = false;
    } else if (!/^\d{10}$/.test(formData.contact)) {
      newErrors.contact = "Contact number must be 10 digits.";
      isValid = false;
    }

    // Description validation
    if (!formData.description.trim()) {
      newErrors.description = "Description is required.";
      isValid = false;
    }

    // State validation
    if (!formData.state) {
      newErrors.state = "Please select a state.";
      isValid = false;
    }

    // City validation
    if (!formData.city) {
      newErrors.city = "Please select a city.";
      isValid = false;
    }
    
    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitMessage({ message: "", isError: false }); // Clear previous message
    
    if (validateForm()) {
      // All data is valid, now we can submit to the API
      console.log("Form submitted successfully:", formData);
      try {
        // You would place your real API call here
        // const res = await fetch("http://localhost/react-api/submit.php", {
        //   method: "POST",
        //   headers: { "Content-Type": "application/json" },
        //   body: JSON.stringify(formData),
        // });
        // const result = await res.json();
        // console.log("API response:", result);

        // Simulating a successful API response
        await new Promise(resolve => setTimeout(resolve, 1000));
        setSubmitMessage({
          message: "Your request has been submitted successfully!",
          isError: false,
        });

      } catch (err) {
        console.error("Submission error:", err);
        setSubmitMessage({
          message: "There was an error submitting your request. Please try again.",
          isError: true,
        });
      }
    } else {
      console.log("Form has validation errors.");
      setSubmitMessage({
        message: "Please correct the errors in the form.",
        isError: true,
      });
    }
  };

  return (
    <>
      <style>
        {`
          /* Modal Styles */
          .modal-overlay {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(15, 23, 42, 0.8);
            display: flex;
            align-items: center;
            justify-content: center;
            z-index: 1000;
            backdrop-filter: blur(6px);
            animation: fadeIn 0.3s ease-in-out;
          }

          .modal {
            background: #ffffff;
            border-radius: 16px;
            padding: 2rem;
            width: 90%;
            max-width: 600px;
            box-shadow: 0 12px 40px rgba(0, 0, 0, 0.2);
            text-align: center;
            position: relative;
            animation: slideUp 0.4s ease;
            max-height: 90vh;
            overflow-y: auto;
          }

          .close-btn {
            position: absolute;
            top: 15px;
            right: 20px;
            font-size: 28px;
            background: none;
            border: none;
            cursor: pointer;
            color: #555;
            transition: transform 0.2s ease;
          }
          .close-btn:hover {
            transform: scale(1.2);
            color: #000;
          }

          /* Header */
          .modal-header {
            margin-bottom: 2rem;
          }
          .company-logo {
            height: 60px;
            margin: 0 auto 10px;
          }
          .modal-header h2 {
            font-size: 2rem;
            font-weight: 700;
            color: #1f2937;
            margin-bottom: 0.5rem;
          }
          .modal-header .subtitle {
            font-size: 1rem;
            color: #4b5563;
          }
          
          /* Benefits Section */
          .benefits-section {
            background-color: #f3f4f6;
            border-radius: 12px;
            padding: 1.5rem;
            margin-bottom: 2rem;
            text-align: left;
          }
          .benefits-section h3 {
            font-size: 1.25rem;
            font-weight: 600;
            color: #1f2937;
            margin-bottom: 1rem;
          }
          .benefits-section ul {
            list-style: none;
            padding-left: 0;
          }
          .benefits-section li {
            position: relative;
            padding-left: 1.5rem;
            color: #4b5563;
            margin-bottom: 0.5rem;
          }
          .benefits-section li::before {
            content: "✓";
            position: absolute;
            left: 0;
            color: #2563eb;
            font-weight: bold;
          }

          /* Form Styles */
          .booking-form {
            display: flex;
            flex-direction: column;
            gap: 1.5rem;
          }
          .form-row {
            display: flex;
            flex-direction: column;
            gap: 1.5rem;
          }
          @media (min-width: 768px) {
            .form-row {
              flex-direction: row;
            }
          }
          .form-group {
            width: 100%;
          }
          .form-row input, .form-row textarea, .form-row select {
            width: 100%;
            padding: 0.75rem;
            border-radius: 8px;
            border: 1px solid #d1d5db;
            font-size: 1rem;
            transition: border-color 0.2s ease, box-shadow 0.2s ease;
          }
          .form-row input:focus, .form-row textarea:focus, .form-row select:focus {
            outline: none;
            border-color: #2563eb;
            box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.2);
          }
          .error-message {
            color: #ef4444;
            font-size: 0.875rem;
            margin-top: 0.5rem;
            text-align: left;
          }
          
          .submit-btn {
            padding: 1rem;
            font-size: 1.125rem;
            font-weight: 600;
            background: #2563eb;
            color: #fff;
            border: none;
            border-radius: 8px;
            cursor: pointer;
            transition: all 0.25s ease;
          }
          .submit-btn:hover {
            background: #1d4ed8;
            transform: translateY(-2px);
          }
          .submit-btn:disabled {
            background-color: #9ca3af;
            cursor: not-allowed;
          }
          
          .submit-message {
            padding: 1rem;
            margin-top: 1rem;
            border-radius: 8px;
            font-weight: 600;
            text-align: center;
          }
          .submit-message.success {
            background-color: #d1fae5;
            color: #065f46;
          }
          .submit-message.error {
            background-color: #fee2e2;
            color: #991b1b;
          }

          /* Animations */
          @keyframes fadeIn {
            from { opacity: 0; }
            to { opacity: 1; }
          }
          @keyframes slideUp {
            from { transform: translateY(30px); opacity: 0; }
            to { transform: translateY(0); opacity: 1; }
          }
        `}
      </style>
      <div className="modal-overlay">
        <div className="modal" ref={modalRef}>
          <button className="close-btn" onClick={onClose}>
            &times;
          </button>

          {/* Company Logo and Header */}
          <div className="modal-header">
            <img src="https://placehold.co/100x60/333/fff?text=FRANMAX" alt="Franmax Expo Logo" className="company-logo" />
            <h2>Book Your Stall</h2>
            <p className="subtitle">Secure your spot at Franmax Expo 2025</p>
          </div>
          
          {/* Submission Message */}
          {submitMessage.message && (
            <div className={`submit-message ${submitMessage.isError ? "error" : "success"}`}>
              {submitMessage.message}
            </div>
          )}

          {/* Why Book Section */}
          <div className="benefits-section">
            <h3>Why Exhibit With Us?</h3>
            <ul>
              <li>Connect with top investors and franchise owners</li>
              <li>Showcase your brand to 500+ visitors</li>
              <li>Expand your business across India</li>
              <li>Get exclusive media coverage & leads</li>
            </ul>
          </div>

          {/* Booking Form */}
          <form onSubmit={handleSubmit} className="booking-form">
            <div className="form-row">
              <div className="form-group">
                <input
                  type="text"
                  name="name"
                  placeholder="Name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
                {errors.name && <div className="error-message">{errors.name}</div>}
              </div>
              <div className="form-group">
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
                {errors.email && <div className="error-message">{errors.email}</div>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <input
                  type="tel"
                  name="contact"
                  placeholder="Contact Number"
                  value={formData.contact}
                  onChange={handleChange}
                  required
                />
                {errors.contact && <div className="error-message">{errors.contact}</div>}
              </div>
              <div className="form-group">
                <textarea
                  name="description"
                  placeholder="Description"
                  rows="2"
                  value={formData.description}
                  onChange={handleChange}
                  required
                />
                {errors.description && <div className="error-message">{errors.description}</div>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <select
                  name="state"
                  value={formData.state}
                  onChange={handleChange}
                  disabled={loadingStates}
                  required
                >
                  <option value="">{loadingStates ? "Loading States..." : "Select State"}</option>
                  {states.map((state) => (
                    <option key={state.value} value={state.value}>
                      {state.label}
                    </option>
                  ))}
                </select>
                {errors.state && <div className="error-message">{errors.state}</div>}
              </div>
              <div className="form-group">
                <select
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                  disabled={!formData.state || loadingCities}
                  required
                >
                  <option value="">{loadingCities ? "Loading Cities..." : "Select City"}</option>
                  {cities.map((city) => (
                    <option key={city.value} value={city.value}>
                      {city.label}
                    </option>
                  ))}
                </select>
                {errors.city && <div className="error-message">{errors.city}</div>}
              </div>
            </div>

            <button type="submit" className="submit-btn" disabled={loadingStates || loadingCities}>
              Submit
            </button>
          </form>
        </div>
      </div>
    </>
  );
};

export default App;
