import React from 'react';
import { Home, Info, Briefcase, Mail } from 'lucide-react';

/**
 * A reusable link component for the navigation menu with an optional icon.
 */
const NavLink = ({ href, children, Icon, ...props }) => (
  <a
    href={href}
    className="flex items-center space-x-2 text-gray-600 hover:text-indigo-600 transition-colors duration-200 font-medium"
    {...props}
  >
    {Icon && <Icon size={20} />}
    <span>{children}</span>
  </a>
);

/**
 * A responsive and fixed-position header component with a navigation menu.
 * The navbar stays at the top of the page as the user scrolls.
 */
function Header() {
  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-white/80 backdrop-blur-md shadow-sm">
      <nav className="container mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
        {/* Brand/Logo with a home icon */}
        <div className="flex-shrink-0">
          <NavLink href="#" Icon={Home}>OnePager</NavLink>
        </div>

        {/* Desktop Navigation (always visible) */}
        <div className="flex items-center space-x-8">
          <NavLink href="#about" Icon={Info}>About</NavLink>
          <NavLink href="#services" Icon={Briefcase}>Services</NavLink>
          <NavLink href="#contact" Icon={Mail}>Contact</NavLink>
        </div>
      </nav>
    </header>
  );
}

// The main App component which composes the entire website.
export default function App() {
  return (
    <div className="bg-gray-50 font-sans text-gray-900 antialiased">
      <Header />
      <main className="pt-24 min-h-screen">
        {/* Main content will go here */}
        <div className="container mx-auto px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-center">Your main page content starts here.</h1>
          {/* Example sections can be added here */}
        </div>
      </main>
    </div>
  );
}
