import React, { useState, useEffect, useRef } from 'react';
import './Count.css'; // Import the CSS file

const DraggableCountdownBox = () => {
    // State for the countdown timer
    const [days, setDays] = useState('00');
    const [hours, setHours] = useState('00');
    const [minutes, setMinutes] = useState('00');
    const [seconds, setSeconds] = useState('00');

    // State for drag functionality
    const [isDragging, setIsDragging] = useState(false);
    const [position, setPosition] = useState({ x: 20, y: 20 });
    const dragStart = useRef({ x: 0, y: 0 });
    const boxRef = useRef(null);

    // Effect for the countdown timer
    useEffect(() => {
        const eventDate = new Date("September 14, 2025 00:00:00").getTime();

        const interval = setInterval(() => {
            const now = new Date().getTime();
            const distance = eventDate - now;

            if (distance < 0) {
                clearInterval(interval);
                // Handle event started state if needed
                return;
            }

            const d = Math.floor(distance / (1000 * 60 * 60 * 24));
            const h = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            const m = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
            const s = Math.floor((distance % (1000 * 60)) / 1000);

            setDays(String(d).padStart(2, '0'));
            setHours(String(h).padStart(2, '0'));
            setMinutes(String(m).padStart(2, '0'));
            setSeconds(String(s).padStart(2, '0'));
        }, 1000);

        // Cleanup function for the interval
        return () => clearInterval(interval);
    }, []);

    // Effect for handling drag-and-drop
    useEffect(() => {
        const handleMouseMove = (e) => {
            if (!isDragging) return;

            const newX = e.clientX - dragStart.current.x;
            const newY = e.clientY - dragStart.current.y;
            
            const boxWidth = boxRef.current.offsetWidth;
            const boxHeight = boxRef.current.offsetHeight;
            const viewportWidth = window.innerWidth;
            const viewportHeight = window.innerHeight;

            // Clamp the position to stay within the viewport
            const clampedX = Math.max(0, Math.min(newX, viewportWidth - boxWidth));
            const clampedY = Math.max(0, Math.min(newY, viewportHeight - boxHeight));

            setPosition({ x: clampedX, y: clampedY });
        };

        const handleMouseUp = () => {
            if (isDragging) {
                setIsDragging(false);
            }
        };

        if (isDragging) {
            window.addEventListener('mousemove', handleMouseMove);
            window.addEventListener('mouseup', handleMouseUp);
        }

        // Cleanup function to remove event listeners
        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
            window.removeEventListener('mouseup', handleMouseUp);
        };
    }, [isDragging]);

    const handleMouseDown = (e) => {
        setIsDragging(true);
        const rect = boxRef.current.getBoundingClientRect();
        dragStart.current = {
            x: e.clientX - rect.left,
            y: e.clientY - rect.top,
        };
        e.preventDefault(); // Prevent default browser behavior like text selection
    };

    return (
        <div
            ref={boxRef}
            className={`countdown-box ${isDragging ? 'dragging' : ''}`}
            style={{
                left: `${position.x}px`,
                top: `${position.y}px`,
                // We use transform for smoother animation and better performance,
                // but for simplicity and directness, `left` and `top` are fine.
                // The CSS position is `fixed`, so `left` and `top` are relative to the viewport.
            }}
            onMouseDown={handleMouseDown}
        >
            <h3>Event Starts In:</h3>
            <div className="countdown-timer">
                <div className="time-segment">
                    <span>{days}</span>
                    <small>Days</small>
                </div>
                <div className="time-segment">
                    <span>{hours}</span>
                    <small>Hours</small>
                </div>
                <div className="time-segment">
                    <span>{minutes}</span>
                    <small>Minutes</small>
                </div>
                <div className="time-segment">
                    <span>{seconds}</span>
                    <small>Seconds</small>
                </div>
            </div>
            <a href="#" className="add-calendar-button">Add to Calendar</a>
        </div>
    );
};

export default DraggableCountdownBox;
