import React, { useState, useEffect } from "react";
import "./Banner.css";

const Banner = () => {
  const images = [
    "https://erpnews.com/v2/wp-content/uploads/2022/09/Corporate-Event-1.webp",
    "https://media.istockphoto.com/id/499517325/photo/a-man-speaking-at-a-business-conference.jpg?s=612x612&w=0&k=20&c=gWTTDs_Hl6AEGOunoQ2LsjrcTJkknf9G8BGqsywyEtE=",
   
  ]; // put your image paths here

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, 4000); // 4 seconds per image
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div
      className="banner"
      style={{ backgroundImage: `url(${images[currentIndex]})` }}
    >
      <div className="overlay">
        <h1>Good For Brands, Great For Business</h1>
        <p>Showcase your products at Franmax Expo 2025</p>
        <a href="/book-stall" className="banner-btn">
          Book Your Stall
        </a>
      </div>
    </div>
  );
};

export default Banner;
