import React from "react";
import { motion } from "framer-motion";
import { GiThinking } from "react-icons/gi"; // Human thinking icon
import './HomePage.css';

import Banner from './Banner';
import AboutSection from './AboutSection';
import ExclusiveRights from "./ExclusiveRights";
import ContactSection from "./ContactSection";
import OurPartners from "./OurPartners";
import CountdownTimer from "./CountdownTimer";

const sectionVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.8, ease: "easeOut" } }
};

const Homepage = () => {
  return (
    <div className="homepage">
      <main className="main-content">

        <motion.div initial="hidden" animate="visible" variants={sectionVariants}>
          <Banner />
        </motion.div>

        <motion.div initial="hidden" whileInView="visible" viewport={{ once: true, amount: 0.2 }} variants={sectionVariants}>
          <AboutSection />
        </motion.div>

        <motion.div initial="hidden" whileInView="visible" viewport={{ once: true, amount: 0.2 }} variants={sectionVariants}>
          <ExclusiveRights />
        </motion.div>

        <motion.div initial="hidden" whileInView="visible" viewport={{ once: true, amount: 0.2 }} variants={sectionVariants}>
          <CountdownTimer />
        </motion.div>

        <motion.div initial="hidden" whileInView="visible" viewport={{ once: true, amount: 0.2 }} variants={sectionVariants}>
          <OurPartners />
        </motion.div>

        <motion.div initial="hidden" whileInView="visible" viewport={{ once: true, amount: 0.2 }} variants={sectionVariants}>
          <ContactSection />
        </motion.div>

      </main>

      {/* ================= Draggable Human Thinking Icon ================= */}
      <motion.a
        href="https://franmaxindia.com"
        target="_blank"
        rel="noopener noreferrer"
        drag
        dragMomentum={true} // Smooth drag with momentum
        dragElastic={0.4} // Slight elastic movement
        className="floating-icon"
        whileHover={{ scale: 1.2 }}
        whileTap={{ scale: 0.9 }}
      >
        <GiThinking size={45} color="#1a73e8" /> {/* Smaller human thinking icon */}
      </motion.a>
    </div>
  );
};

export default Homepage;
